import React from "react";
import { useNavigate } from "react-router-dom";
import { ref, uploadBytes } from "firebase/storage"; // Import Firebase Storage utilities
import { storage } from "./firebase"; // Import Firebase configuration

function Home() {
    const navigate = useNavigate();

    // Handle file upload
    const handleFileUpload = async (event) => {
        const file = event.target.files[0]; // Get the selected file

        if (file) {
            // Create a Firebase Storage reference
            const storageRef = ref(storage, `pdfs/${file.name}`);

            try {
                // Upload the file to Firebase Storage
                await uploadBytes(storageRef, file);
                console.log(`File uploaded successfully: ${file.name}`);
                alert("File uploaded successfully!");

                // Redirect to the "Visualization" page
                navigate("/visualization");
            } catch (error) {
                console.error("Error uploading file:", error);
                alert("Error uploading file. Please try again.");
            }
        } else {
            alert("Please select a PDF file to upload.");
        }
    };

    return (
        <div style={styles.container}>
            <h1 style={styles.title}>MedCo</h1>
            <div style={styles.uploadSection}>
                <label htmlFor="pdfUpload" style={styles.uploadLabel}>
                    Upload PDF
                </label>
                <input
                    type="file"
                    id="pdfUpload"
                    accept=".pdf"
                    onChange={handleFileUpload}
                    style={styles.uploadInput}
                />
            </div>
        </div>
    );
}

// Inline styles
const styles = {
    container: {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        height: "100vh",
        backgroundColor: "#e0f7f7", // Light teal background
    },
    title: {
        fontSize: "2rem",
        color: "#008080", // Teal color for the title
        marginBottom: "20px",
    },
    uploadSection: {
        textAlign: "center",
    },
    uploadLabel: {
        display: "inline-block",
        padding: "10px 20px",
        backgroundColor: "#008080",
        color: "white",
        fontSize: "16px",
        borderRadius: "5px",
        cursor: "pointer",
    },
    uploadInput: {
        display: "none", // Hide the default file input
    },
};

export default Home;
