import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { supabase } from "./supabaseClient"; // Make sure this file is correctly set up

function Register() {
    const navigate = useNavigate();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [name, setName] = useState(""); // New state for user's name
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);

    const handleRegister = async (event) => {
        event.preventDefault();

        setLoading(true);
        setError("");

        try {
            // Create the user with email and password
            const { user, error: signupError } = await supabase.auth.signUp({
                email,
                password,
            });

            if (signupError) throw signupError;

            // Once the user is created, update their user metadata with the name
            const { error: updateError } = await supabase
                .from("users")
                .upsert([
                    {
                        id: user.id,
                        full_name: name, // Store the name in the user metadata
                    },
                ]);

            if (updateError) throw updateError;

            // Redirect to the home page or login page after successful registration
            navigate("/login");
        } catch (error) {
            setError(error.message);
            setLoading(false);
        }
    };

    return (
        <div style={styles.container}>
            <h1 style={styles.title}>Register</h1>
            <form onSubmit={handleRegister} style={styles.form}>
                <label htmlFor="name" style={styles.label}>User Name</label>
                <input
                    type="text"
                    id="name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                    style={styles.input}
                />
                <label htmlFor="email" style={styles.label}>Email</label>
                <input
                    type="email"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                    style={styles.input}
                />
                <label htmlFor="password" style={styles.label}>Password</label>
                <input
                    type="password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                    style={styles.input}
                />
                <button type="submit" style={styles.button} disabled={loading}>
                    {loading ? "Registering..." : "Register"}
                </button>
                {error && <p style={styles.error}>{error}</p>}
            </form>
        </div>
    );
}

// Inline Styles
const styles = {
    container: {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        height: "100vh",
        backgroundColor: "#e0f7f7", // Background color matching your design
        padding: "0px",
    },
    title: {
        fontSize: "2rem",
        color: "#008080", // Teal color for the title
        marginBottom: "20px",
    },
    form: {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        width: "100%",
        maxWidth: "400px",
    },
    label: {
        fontSize: "16px",
        marginBottom: "5px",
        color: "#008080", // Teal color for labels
    },
    input: {
        padding: "10px",
        fontSize: "16px",
        marginBottom: "15px",
        borderRadius: "5px",
        border: "1px solid #ccc",
        width: "100%",
    },
    button: {
        padding: "10px 20px",
        fontSize: "16px",
        backgroundColor: "#008080",
        color: "white",
        border: "none",
        borderRadius: "5px",
        cursor: "pointer",
        width: "100%",
    },
    error: {
        color: "red",
        fontSize: "14px",
        marginTop: "10px",
    },
};

export default Register;
