import React from "react";
import { useNavigate } from "react-router-dom";

function Home() {
    const navigate = useNavigate();

    // Handle navigation to Sign In and Register pages
    const handleSignIn = () => {
        navigate("/signin");
    };

    const handleRegister = () => {
        navigate("/register");
    };

    return (
        <div style={styles.container}>
            {/* Left Section: MedCo with Background Image */}
            <div style={{ ...styles.leftSection, backgroundImage: `url('/assets/bg1.png')` }}>
                <h1 style={styles.title}>MedCo</h1>
            </div>

            {/* Vertical Divider */}
            <div style={styles.divider}></div>

            {/* Right Section: Buttons */}
            <div style={styles.rightSection}>
                <h2 style={styles.subtitle}>Welcome</h2>
                <p style={styles.description}>Please Sign In or Register to continue.</p>
                <div style={styles.buttonsContainer}>
                    <button onClick={handleSignIn} style={styles.button}>
                        Sign In
                    </button>
                    <button onClick={handleRegister} style={{ ...styles.button, ...styles.registerButton }}>
                        Register
                    </button>
                </div>
            </div>
        </div>
    );
}

// Inline Styles
const styles = {
    container: {
        display: "flex",
        flexDirection: "row",
        height: "100vh",
        width: "100vw",
        backgroundColor: "#e0f7f7",
    },
    leftSection: {
        flex: 1,
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        color: "white",
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
    },
    title: {
        fontSize: "6rem",
        fontWeight: "bold",
        textShadow: "2px 2px 4px rgba(0, 0, 0, 0.7)",
    },
    divider: {
        width: "2px",
        backgroundColor: "#ccc",
        height: "80%",
        alignSelf: "center",
    },
    rightSection: {
        flex: 1,
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        padding: "20px",
    },
    subtitle: {
        fontSize: "2rem",
        color: "#008080",
        marginBottom: "10px",
    },
    description: {
        fontSize: "1rem",
        color: "#555",
        marginBottom: "20px",
        textAlign: "center",
    },
    buttonsContainer: {
        display: "flex",
        flexDirection: "column",
        gap: "15px",
        width: "100%",
        maxWidth: "300px",
    },
    button: {
        padding: "10px 20px",
        fontSize: "16px",
        backgroundColor: "#008080",
        color: "white",
        border: "none",
        borderRadius: "5px",
        cursor: "pointer",
    },
    registerButton: {
        backgroundColor: "#006666",
    },
};

export default Home;
